const sort = (setFilter, sort, setSort, todos) => {
  let filtTodo
  if (sort == 'all') {
    filtTodo = todos
  }
  if (sort == 'todo') {
    filtTodo = todos.filter(todo => todo.completed === false)
  }
  if (sort == 'done') {
    filtTodo = todos.filter(todo => todo.completed === true)
  }
  setFilter(filtTodo)

}

export default sort
