import React, { useState } from "react"

const TaskList = (props) => {
  const { todos, setTodos } = props;

  return (
    todos.map((todo, index) => {
      const deleteTask = (e, id) => {
        console.log(id);
        e.preventDefault();

        setTodos(todos.slice(todos.indexOf(todos.filter(todo => todo.id == id)), 1))


      }
      return (
        <li>
          {todo.title}
          <button onClick={(e, id) => deleteTask(e, id)}>&#10060;</button>
        </li>
      )
    })

  )
}




export default TaskList;
