import React, { useState } from "react";
import { v4 as uuidv4 } from 'uuid';

const TodoForm = (props) => {
  const { todos, setTodos, filter, setFilter } = props;
  const [text, setText] = useState('');
  const [toggleAll, setToggleAll] = useState(false)




  const saveTask = (e) => {
    e.preventDefault();
    if (text !== '') {
      const todo = {
        id: uuidv4(),
        title: text,
        completed: false,
      }
      setText("");
      setTodos([todo, ...todos]);
      setFilter([todo, ...todos])
    }
  }


  const deleteAll = () => {
    const newTodo = todos.filter(todo => todo.completed !== true)
    setTodos(newTodo);
    setFilter(newTodo);
  }

  const completeAll = () => {

    const toggleAllCheck = todos.filter(todo => todo.completed == false)

    console.log(toggleAllCheck)

    if (toggleAllCheck) {
      setToggleAll(true)
    } else {
      setToggleAll(false)
    }
    console.log(toggleAll)

    let completeTodo = todos.map((item) => ({
      ...item,
      completed: toggleAll
    }))

    setTodos(completeTodo)
    setFilter(completeTodo)

  }


  return (
    <form className="create-form" onSubmit={(e) => saveTask(e)}>
      <input type='checkbox' onChange={() => completeAll()} checked={toggleAll} />
      <input className="create-input" autoFocus value={text} onChange={(e) => setText(e.target.value)} type="text" placeholder="What needs to be done?" />
      <button type="button" onClick={() => deleteAll()}>&#10006;</button>
    </form>
  )
}
export default TodoForm;
