import { createSelector } from "@reduxjs/toolkit";

const filter = (state) => state.todos.filter
const todos = (state) => state.todos.todos

export const filteredTodos = createSelector(
  [filter, todos],
  (filter, todos) => {
    switch (filter) {
      case 'all':
        return todos
      case 'active':
        return (todos.filter(todo => !todo.completed))
      case 'done':
        return (todos.filter(todo => todo.completed))
      default:
        break;
    }
  }
)
