import React from "react";
import styles from "./Buttons.module.css"


const Buttons = (props) => {

  const deleteAll = () => {
    const newTodo = ptodos.filter(todo => todo.completed === false)
    setTodos(newTodo);
    setToggleAll(false)
  }


  if (props.todos.length) {

    return (

      <div className={styles.btns}>
        <div className={styles.span}>
          <span
            className={styles.btnOn}>
            {props.todos.length}
          </span>tasks
        </div>
        <div className={styles.filters}>
          <button
            className={props.filter === 'all' ? styles.btnOn : styles.btnOff}
            onClick={() => {
              props.setFilter('all')
            }}>all</button>
          <button
            className={props.filter === 'active' ? styles.btnOn : styles.btnOff}
            onClick={() => {
              props.setFilter('active')
            }}>active</button>
          <button
            className={props.filter === 'done' ? styles.btnOn : styles.btnOff}
            type='button' onClick={() => {
              props.setFilter('done')
            }}>done</button>
        </div>
        <div className={styles.deleteAll}>
          <button className={styles.btnOff}

            type="button"
            onClick={() => props.deleteAll()}
          >
            clear
          </button>
        </div>
      </div>
    )
  }
}

export default Buttons;
