import React from "react";



const TaskItem = (props) => {

  return (
    <li>
      <form 
      className="rename-form" 
      onSubmit={(e) => props.renameTask(e)} >
        <label >
          <input 
          type='checkbox' 
          onChange={() => props.completeTask()} 
          checked={props.todo.completed} />
          {props.edit ? (
            <input 
            value={props.text} 
            onChange={(e) => props.setText(e.target.value)} 
            onBlur={(e) => props.setEdit(false)} 
            type="text" 
            autoFocus></input>
          ) : (
            <div onDoubleClick={(e) => props.setEdit(true)}>{props.todo.title}</div>
          )}
        </label>
        <button type="button" onClick={() => props.deleteTask(props.todo.id)}>&#10006;</button>
      </form>
    </li>
  )
}

export default TaskItem;
