import React, { useState, useEffect } from "react";
import { v4 as uuidv4 } from 'uuid';

import Header from './components/Header';
import TodoForm from './components/TodoForm';
import TaskList from './components/TaskList';
import Buttons from './components/Buttons';
import Footer from './components/Footer';
import localToggleAll from "./utills/localToggleAll";



function App() {

  const [todos, setTodos] = useState(localTodos);
  const [filter, setFilter] = useState('all');
  const [text, setText] = useState('');

  useEffect(() => {
    localStorage.setItem('todos', JSON.stringify(todos));
  }, [todos]);

  const localTodos = () => {
    if (localStorage.getItem('todos') == null) {
      return []
    } else {
      return JSON.parse(localStorage.getItem('todos'))
    }
  }

  const [toggleAll, setToggleAll] = useState(localToggleAll)
  useEffect(() => {
    localStorage.setItem('toggleAll', JSON.stringify(toggleAll));
  }, [toggleAll]);

  const createNewTodo = (text) => {
    const todo = {
      id: uuidv4(),
      title: text,
      completed: false,
    }
    setTodos([todo, ...todos]);
  }

  const saveTask = (e) => {
    e.preventDefault();
    if (!text) { return };
    props.createNewTodo(text)
    setText("");
  }

  const completeAll = () => {
    const toggleAllCheck = !todos.every((todo) => todo.completed)
    let completeTodo = todos.map((item) => ({
      ...item,
      completed: toggleAllCheck
    }))
    setToggleAll(toggleAllCheck)
    setTodos(completeTodo)
    setFilter(completeTodo)
  }

  const deleteTask = (id) => {
    const newTodo = todos.filter(todo => todo.id !== id)
    setTodos(newTodo);

  }

  const renameTask = (e) => {
    e.preventDefault();
    if (text !== '') {
      todo.title = text;
      localStorage.setItem('todos', JSON.stringify(todos))
      setEdit(false)
    }
  }

  const completeTask = () => {


    todo.completed = !todo.completed
    localStorage.setItem('todos', JSON.stringify(todos))
    setToggle(!toggle)
    const toggleAllCheck = todos.every((todo) => todo.completed)
    if (toggleAllCheck) {
      setToggleAll(true)
    } else {
      setToggleAll(false)
    }

  }



  return (
    <div className="container">
      <Header />
      <TodoForm
        todos={todos}
        setTodos={setTodos}
        filter={filter}
        setFilter={setFilter}
        toggleAll={toggleAll}
        setToggleAll={setToggleAll}
        createNewTodo={createNewTodo}
      />
      <TaskList
        todos={todos}
        setTodos={setTodos}
        filter={filter}
        setFilter={setFilter}
        sort={sort}
        setSort={setSort}
        toggleAll={toggleAll}
        setToggleAll={setToggleAll}
      />
      <Buttons todos={todos}
        setTodos={setTodos}
        filter={filter}
        setFilter={setFilter}
        sort={sort}
        setSort={setSort}
        setToggleAll={setToggleAll}
      />
      <Footer />
    </div>
  );
}

export default App;
