import React, { useState, useEffect } from "react";


import Header from './components/Header';
import TodoForm from './components/TodoForm';
import TaskList from './components/TaskList';
import Buttons from './components/Buttons';
import Footer from './components/Footer';
import useLocal from "./utills/useLocal";

function App() {
  const [todos, setTodos] = useState(useLocal);
  useEffect(() => {
    localStorage.setItem('todos', JSON.stringify(todos));
  }, [todos]);
  const [filter, setFilter] = useState(todos)
  const [sort, setSort] = useState('all')
  const [toggleAll, setToggleAll] = useState(false)

  return (
    <div className="container">
      <Header />
      <TodoForm todos={todos} setTodos={setTodos} filter={filter} setFilter={setFilter} />
      <TaskList todos={todos} setTodos={setTodos} filter={filter} setFilter={setFilter} sort={sort} setSort={setSort} toggle/>
      <Buttons todos={todos} setTodos={setTodos} filter={filter} setFilter={setFilter} sort={sort} setSort={setSort} />
      <Footer />
    </div>
  );
}

export default App;
