import React, { useState } from "react";

const TaskItem = (props) => {
  const { todos, setTodos, todo } = props;
  const [text, setText] = useState('');
  const deleteTask = (id) => {

    const newTodo = todos.filter(todo => todo.id != id)

    setTodos(newTodo);


  }

  const changeTask = (title) => {
    return (
      <form>
        <input value={title} onChange={(e) => (e.target.value)} type="text" />
        <button onClick={() => renameTask(title)}>rename</button>
      </form>
    )
  }

  const renameTask = (title) => {

    todo.title = title



  }

  return (
    <li onDoubleClick={() => changeTask()}>
      {todo.title}
      <button onClick={() => deleteTask(todo.id)}>&#10006;</button>
    </li>
  )
}

export default TaskItem;
