export const todos = (state = [], action) => {
  switch (action.type) {
    case 'CREATE_TODO':
      return [
        ...state,
        {
          id: action.id,
          text: action.text,
          completed: false
        }
      ]
case 'TODO_COMPLETE':
  return state.map(todo =>
    todo.id === action.id ? {
      ...todo, 
      completed: !todo.completed
    } : 
    todo)
    default:
      return state
  }
}
