import { createSlice } from "@reduxjs/toolkit";
import { v4 as uuidv4 } from 'uuid';
import { localFilter } from "./localStorageData";
import { localTodos } from "./localStorageData";

const initialState = () => ({
  filter: localFilter,
  todos: localTodos,
})

export const todosSlice = createSlice({
  name: 'todos',
  initialState,
  reducers: {

    createTodo: (state, action) => {
      state.todos.unshift(
        {
          id: uuidv4(),
          completed: false,
          title: action.payload,
        }
      )
    },

    completeTodo: (state, action) => {
      const index = (state.todos).findIndex(todo => todo.id === action.id)
      state.todos = (state.todos).splice(index, 1, {
        ...todo,
        completed: !state.todos.completed
      })
      // state.todos.map(todo =>
      //   todo.id === action.id ? {
      //     ...todo,
      //     completed: !todo.completed
      //   } :
      //     todo)
    },

    completeAllTodo: state => {
      const isComplete = state.todos.every((todo) => todo.completed)
      state.todos = state.todos.map((todo) => ({
        ...todo,
        completed: !isComplete
      }))
    },

    deleteTodo: (state, action) => { },

    deleteDoneTodo: state => {
      state.todos = (state.todos).filter(todo => todo.completed === false)
      console.log(state.todos)
    },

    renameTodo: (state, action) => { },

    setFilter: (state, action) => {
      state.filter = action.payload
    }

  }
})


export const {
  createTodo,
  completeAllTodo,
  completeTodo,
  deleteTodo,
  deleteDoneTodo,
  renameTodo,
  setFilter
} = todosSlice.actions

export default todosSlice.reducer
