import { createSlice } from "@reduxjs/toolkit";
import { v4 as uuidv4 } from 'uuid';
import { localFilter } from "./localStorageData";
import { localTodos } from "./localStorageData";

const initialState = () => ({
  filters: localFilter,
  todos: localTodos,
})

const newTodos = (state) => state.todos
const newFilter = (state) => state.filter
console.log (newTodos, newFilter)
localStorage.setItem('todos', JSON.stringify(newTodos));
localStorage.setItem('filter', JSON.stringify(newFilter));



export const todosSlice = createSlice({
  name: 'todos',
  initialState,
  reducers: {

    createTodo: (state, action) => {
      state.todos.push(
        {
          id: uuidv4(),
          title: action.payload,
          completed: false
        }
      )
    },

    completeTodo: (state, action) => {
      state.todos.map(todo =>
        todo.id === action.id ? {
          ...todo,
          completed: !todo.completed
        } :
          todo)
    },

    completeAllTodo: state => {
      const isComplete = state.todos.every((todo) => todo.completed)
      state.todos = state.todos.map((todo) => ({
        ...todo,
        completed: !isComplete
      }))
    },

    deleteTodo: (state, action) => { },

    deleteDoneTodo: state => {
      state.todos.filter(todo => todo.completed === true)
    },

    renameTodo: (state, action) => { },

  }
})


export const { createTodo, completeAllTodo, completeTodo, deleteTodo, deleteDoneTodo, renameTodo } = todosSlice.actions

export default todosSlice.reducer
