import React, { useState, useEffect, useMemo } from "react";

import styles from "./App.module.css"

import Header from './components/Header/Header';
import TodoForm from './components/TodoForm/TodoForm';
import TaskList from './components/TaskList/TaskList';
import Buttons from './components/Buttons/Buttons';
import Footer from './components/Footer/Footer';



function App() {

  const localLoad = (key, value) => {
    const item = localStorage.getItem(key)
    if (item == null) {
      return value
    }
    return JSON.parse(item)
  }

  const localTodos = localLoad('todos', [])

  const localFilter = localLoad('filter', 'all')

  // 

  const [todos, setTodos] = useState(localTodos);
  const [filter, setFilter] = useState(localFilter);


  // 

  const UsingEffect = (key, value) => {
    useEffect(() => {
      localStorage.setItem(value, JSON.stringify(key));
    }, [key]);
  }


  useEffect(() => {
    localStorage.setItem('todos', JSON.stringify(todos));}, [todos,filter]);



  UsingEffect(todos, 'todos')
  UsingEffect(filter, 'filter')


  // 



  // 

  // const deleteAll = () => {
  //   const newTodo = todos.filter(todo => todo.completed === false)
  //   setTodos(newTodo);
  //   setToggleAll(false)
  // }



  const filteredTodo = () => {
    switch (filter) {
      case 'all':
        return todos
      case 'active':
        return (todos.filter(todo => !todo.completed))
      case 'done':
        return (todos.filter(todo => todo.completed))
      default:
        break;
    }
  }


  const filteredTodos = useMemo(() => filteredTodo(), [filter, todos])



  return (
    <>
      <div className={styles.container}>
        <Header className={styles.header} />
        <TodoForm
          className={styles.todoForm}
          todos={todos}
          setTodos={setTodos}
        />
        <ul className={styles.ul}>
          <TaskList
            className={styles.taskList}
            todos={filteredTodos}
            setTodos={setTodos}
            realTodos={todos}
          />
        </ul>
        <Buttons
          className={styles.buttons}
          filter={filter}
          setFilter={setFilter}
          todos={filteredTodos}
          realTodos={todos}
        />
      </div>
      <Footer
        todos={filteredTodos}
      />
    </>
  );
}

export default App;
