const sort = (setFilter, sort, todos) => {
  let filtTodo
  switch (sort) {
    case 'all':
      filtTodo = todos
      break;
    case 'todo':
      sortTodo(setFilter, setSort, newTodo)
      break;
    case 'done':
      sortDone(setFilter, setSort, newTodo)
      break;
    default:
      break;
  }
  if (sort === 'all') {
    filtTodo = todos
  }
  if (sort === 'todo') {
    filtTodo = todos.filter(todo => todo.completed === false)
  }
  if (sort === 'done') {
    filtTodo = todos.filter(todo => todo.completed === true)
  }
  setFilter(filtTodo)
}

export default sort
