import React, { useState } from "react";

const TaskItem = (props) => {
  const { todos, setTodos, todo } = props;
  const [text, setText] = useState('');
  const [edit, setEdit] = useState(false);
  const deleteTask = (id) => {

    const newTodo = todos.filter(todo => todo.id != id)

    setTodos(newTodo);


  }
  console.log(edit)

  if (edit) {
    return (
      <li onDoubleClick={() => setEdit(true)}>
        <input value={todo.title}value={text} onChange={(e) => setText(e.target.value)} type="text" placeholder="What needs to be done?"></input>
        <button onClick={() => deleteTask(todo.id)}>&#10006;</button>
      </li>
    )
  } else {
    return (
      <li onDoubleClick={() => setEdit(true)}>
        {todo.title}
        <button onClick={() => deleteTask(todo.id)}>&#10006;</button>
      </li>
    )
  }
}

export default TaskItem;
