import sortList from "../utills/sortList";
import sortAll from "../utills/sortAll";
import sortDone from "../utills/sortDone";
import sortTodo from "../utills/sortTodo";


const Buttons = (props) => {
  const { todos, setTodos, filter, setFilter, setSort, sort, setToggleAll } = props;

  const deleteAll = () => {
    const newTodo = todos.filter(todo => todo.completed !== true)
    setTodos(newTodo);
    setFilter(newTodo);
    setToggleAll(false)
  }

  return (
    <div className="btns">
      <span>{filter.length} tasks left</span>
      <input type='button' onClick={() => {
          sortList(setFilter,sort,todos)
      }} value='all' />
      <input type='button' onClick={() => {
        setSort('todo');
        sortList(setFilter,sort,todos)
      }} value='todo' />
      <input type='button' onClick={() => {
        setSort('done');
        sortList(setFilter,sort,todos)
      }} value='done' />
      <button type="button" onClick={() => deleteAll()}>&#10006;</button>
    </div>
  )
}

export default Buttons;
