import { createSlice } from "@reduxjs/toolkit";
import { v4 as uuidv4 } from 'uuid';

export const todosSlice = createSlice({
  name: 'todos',
  initialState: [localStorage.getItem('todos'), localStorage.getItem('filter')],
  reducers: {

    createTodo: (state, action) => {
     state.push(
        {
          id: uuidv4(),
          title: action.text,
          completed: false
        }
     )
    },

    completeTodo: (state, action) => {
      return state.map(todo =>
        todo.id === action.id ? {
          ...todo,
          completed: !todo.completed
        } :
          todo)
    },

    completeAllTodo: state => { },

    deleteTodo: (state, action) => { },

    deleteDoneTodo: state => {
      return state.filter(todo => todo.completed === true)
    },

    renameTodo: (state, action) => { },

  }
})

export const {createTodo, completeAllTodo, completeTodo, deleteTodo, deleteDoneTodo,renameTodo} = todosSlice.actions

export default todosSlice.reducer
