import { createSlice } from "@reduxjs/toolkit";

export const todosSlice = createSlice({
  name: 'todos',
  initialState: localStorage.getItem('todos'),
  reducers: {
createTodo: state => {
  return [
    ...state,
    {
      id: action.id,
      text: action.text,
      completed: false
    }
  ]
},
  }
})

export default todosSlice.reducer
