const sortList = (setFilter, sort, todos) => {
  const filtTodo = ''
  switch (sort) {
    case 'all':
      filtTodo = todos

    case 'todo':
      filtTodo = todos.filter(todo => todo.completed === false)

    case 'done':
      filtTodo = todos.filter(todo => todo.completed === true)


  }


  setFilter(filtTodo)
}

export default sortList
