const TaskList = (props) => {
  const { todos, setTodos } = props;

  return (
  todos.map((todo, index) => {
    const deleteTask = (id) => {
      e.preventDefault();

      setTodos(todos.filter(todo => todo.id != id))


    }
    return (
      <li>
        {todo.title}
        <button onClick={(e)=>deleteTask(e)}>&#10060;</button>
      </li>
    )
  })

  )
}




