const localLoad = (key, value) => {
  const item = localStorage.getItem(key)
  if (item == undefined) {
    return value
  }
  return JSON.parse(item)
}

export const localTodos = localLoad('todos', [])
export const localFilter = localLoad('filter', 'all')
